mapper.select:
SELECT bno, title, content, writer, regdate, viewcnt 
FROM tbl_board
WHERE bno = #{bno}

mapper.insert:

   SELECT
   NULLIF(MAX(BNO),0)+1 AS BNO
   FROM tbl_board

insert into tbl_board(
   bno
   ,title
   ,content
   ,writer
   ,regdate
   ,viewcnt
) values(
   ${bno}
   ,#{title}
   ,#{content}
   ,#{writer}
   ,now()
   ,0
)

mapper.update:
UPDATE	tbl_board
SET	title = #{title},
		content = #{content},
		writer = #{writer}
WHERE	bno = #{bno}

mapper.select:
SELECT count(*)
 FROM user;
 WHERE id = #{id} AND password = #{passward}
